def get_ipaddr(host="www.163.com"):
    udp_socket = socket.socket(socket.AF_INET, socket.SOCK_DGRAM)
    try:
        udp_socket.connect((host, 9))
        ipaddress = udp_socket.getsockname()[0]
    except socket.error:
        raise
    finally:
        del udp_socket
        return ipaddress

